-- Table names must be lowercase (configured manually with @Table)
-- Column names must be uppercase, because they are automatically processed that way by JDBC
CREATE TABLE IF NOT EXISTS "users" (
    -- This should be compatible with both H2 and Postgre
    "id" BIGSERIAL PRIMARY KEY,
    "username" VARCHAR(255) NOT NULL,
    "firstname" VARCHAR(255) NOT NULL,
    "lastname" VARCHAR(255) NOT NULL,
    "description" TEXT
);

CREATE TABLE IF NOT EXISTS "article" (
    -- This should be compatible with both H2 and PostgreSQL
    "id" BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "title" VARCHAR(255) NOT NULL,
    "headline" VARCHAR(255) NOT NULL,
    "content" TEXT NOT NULL,
    "author_id" BIGINT NOT NULL,
    "slug" VARCHAR(255) NOT NULL,
    "added_at" TIMESTAMP NOT NULL,
    CONSTRAINT "fk_article_author"
    FOREIGN KEY ("author_id")
    REFERENCES "users"("id")
);
